import classNames from 'classnames';
import waves from '../../mixins/waves';
import { mdbDropdown } from './Dropdown';
import { mdbDropdownToggle } from './DropdownToggle';
import { mdbDropdownMenu } from './DropdownMenu';
import { mdbDropdownItem } from './DropdownItem';
import { mdbIcon } from '../Content/Fa';

const Tabs = {
  components: {
    mdbDropdown,
    mdbDropdownToggle,
    mdbDropdownMenu,
    mdbDropdownItem,
    mdbIcon
  },
  props: {
    tag: {
      type: String,
      default: 'ul'
    },
    links: {
      type: [String, Array],
      default: () => []
    },
    active: {
      type: Number,
      default: 0
    },
    content: {
      type: [String, Array]
    },
    color: {
      type: String
    },
    pills: {
      type: Boolean
    },
    tabs: {
      type: Boolean
    },
    vertical: {
      type: Boolean
    },
    justify: {
      type: Boolean
    },
    header: {
      type: Boolean
    },
    navClass: {
      type: String
    },
    card: {
      type: Boolean
    },
    zIndex: {
      type: Number,
      default: 1
    },
    border: {
      type: Boolean
    },
    default: {
      type: Boolean
    },
    fill: {
      type: Boolean
    },
    transitionDuration: {
      type: Number,
      default: 0.3
    },
    transitionStyle: {
      type: String,
      default: "ease-out"
    }
  },
  data() {
    return {
      activeTab: this.active,
      tabLinks: this.links
    };
  },
  computed: {

    filteredLinks() {
      if (typeof this.links === 'string'){
        this.tabLinks.push({text: this.links});
      }
      else {
        this.tabLinks = [...this.links];
      }
      return this.tabLinks.map((link, index) => {
        link.index = index;
        return link;
      }).filter(link => link.index === this.activeTab);
    },
    className() {
      return classNames(
        'nav',
        this.default && 'nav-tabs',
        this.tabs && 'nav-tabs md-tabs',
        this.justify && 'nav-justified',
        this.pills && 'nav-pills md-pills',
        this.fill && 'nav-fill',
        this.vertical && 'flex-column',
        this.pills && this.color ? 'pills-'+this.color : !this.pills && this.color ? 'tabs-'+this.color  : false,
        this.header && 'nav-pills card-header-pills',
        this.navClass
      );
    },
    contentClass() {
      return classNames(
        'tab-content',
        this.card && 'card',
        this.vertical && 'vertical',
        this.border && 'border-right border-bottom border-left rounded-bottom'
      );
    }
  },
  methods: {
    changeTab(index) {
      this.activeTab = index;
      this.$emit('activeTab', this.activeTab);
    },
    beforeEnter(el) {
      el.style.height = '0';
      el.style.opacity = '0';
    },
    enter(el) {
      el.style.height = el.scrollHeight + 'px';
      el.style.opacity = '1';
      el.style.transition = `${this.transitionDuration}s ${this.transitionStyle}`;
    },
    beforeLeave(el) {
      el.style.height = el.scrollHeight + 'px';
      el.style.opacity = '1';
    },
    leave(el) {
      el.style.height = '0';
      el.style.opacity = '0';
      el.style.transition = `${this.transitionDuration}s ${this.transitionStyle}`;

    }
  },
  mixins: [waves]
};

export default Tabs;
export { Tabs as mdbTabs };
